import React from "react";
import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Navbar from "./components/Navbar/Navbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import ForgotPassword from "./pages/ForgotPassword";
import Membership from "./pages/Membership";
import Workspace from "./pages/Workspace";
import PrivateRoute from "./PrivateRoute";

import { Elements, StripeProvider } from "react-stripe-elements";
import Cart from "./components/Cart/Cart";
import CheckoutForm from "./components/CheckoutForm/CheckoutForm";

import { Footer } from "./components/Footer/Footer";

// Stripe Payment
import { loadStripe } from "@stripe/stripe-js";

import { makeStyles } from "@material-ui/core/styles";
import Profile from "./pages/Profile";
import { Create, Height } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    position: "relative",
    minHeight: "100vh",
  },
  footerContainer: {
    backgroundColor: "blue",
    bottom: 0,
    position: "static",
    height: "2.5rem",
  },
}));

const stripePromise = loadStripe("pk_test_6pRNASCoBOKtIshFeQd4XMUh");

class App extends React.Component {
  state = {
    isAuthenticated: false,
    user: null,
  };

  setAuthStatus = (authenticated) => {
    this.setState({ isAuthenticated: authenticated });
  };

  setUser = (user) => {
    this.setState({ user });
  };

  render() {
    const authProps = {
      isAuthenticated: this.state.isAuthenticated,
      user: this.state.user,
      setAuthStatus: this.setAuthStatus,
      setUser: this.setUser,
    };
    return (
      <Router>
        <div>
          <Navbar auth={authProps} />
          <Switch>
            {/* PrivateRoute only allows logged in users to access the the folowing components */}
            {/* <PrivateRoute exact path="/workspace" component={Workspace} />
            <PrivateRoute exact path="/profile" component={Profile} /> */}
            <Route
              exact
              from="/"
              render={(props) => <Home {...props} auth={authProps} />}
            />
            <Route
              exact
              from="/workspace"
              render={(props) => <Workspace {...props} auth={authProps} />}
            />
            <Route
              exact
              path="/membership"
              render={(props) => <Membership {...props} auth={authProps} />}
            />
            <Route
              exact
              path="/profile/:userId"
              render={(props) => <Profile {...props} auth={authProps} />}
            />

            <Route
              exact
              path="/login"
              render={(props) => <Login {...props} auth={authProps} />}
            />
            <Route
              exact
              path="/signup"
              render={(props) => <Signup {...props} auth={authProps} />}
            />
            <Route
              exact
              path="/forgot-password"
              render={(props) => <ForgotPassword {...props} auth={authProps} />}
            />
          </Switch>
          <Footer />
        </div>
      </Router>
    );
  }
}

export default App;
